import React, { useState } from 'react';
import {fetchData} from './api/fetchData';
import Header from './../src/Header';
import AppDrawer from './AppDrawar';


export const App = () => {
  const [query, setQuery] = useState('');
  const [trackings, setTrackings] = useState({});

  const search = async (e) => {
      if(e.key === 'Enter')
      {
        const data = await fetchData(query)
        console.log(data);
        setTrackings(data);
        setQuery('');
      }


  }  
  return (
    <>
      <Header />
      <nput className = 'search' type = 'text' placeholder = "Enter your tracking key" value = {query} onChange = {(e) => setQuery(e.target.value)} onKeyPress = {search}/>
        {trackings.data && (
          <div>
            <h1> {trackings.data.limit}</h1>
          </div>
        )}
        <AppDrawer />
    </>
  );
}


export default App;
