import React, { useState } from 'react';
import {fetchData} from './api/fetchData';
import './App';


export const App = () => {

  const [query, setQuery] = useState('');

  const search = async (e) => {
      if(e.key === 'Enter')
      {
        const data = await fetchData(query)

        console.log(data);
      }  
  }

  return (
    <div className = " App">
      <input 
        type = 'text'
        className = 'search'
        placeholder = "Enter the tracking key"
        value = {query}
        onChange = {(e) => setQuery(e.target.value)}
        onKeyPress = {search}
      />
    </div>
  )
}


export default App;
