import React, { useState } from 'react';
import {fetchData} from './api/fetchData';
import Header from './../src/Header';


export const App = () => {

  const [query, setQuery] = useState('');
  // const [package, setPackage] = useState({});


  const search = async (e) => {
      if(e.key === 'Enter')
      {
        const data = await fetchData(query)

        setPackage(data);
        setQuery('');
      }  
  }

  return (

    <div>
      <Header />
      <input className = 'container' type = 'text' placeholder = "Enter the tracking key" value = {query} onChange = {(e) => setQuery(e.target.value)} onKeyPress = {search}/>
        {/* {package.main && (

        )} */}
    </div>
  )
}


export default App;
